    /**
     * 返回音频文件的 wav字符串 （返回前端） 如果返回值为空，说明调用错误
     * @param text
     * @return
     */
    public static JSONObject text2voice(String text) {
        //url
//        String url ="http://10.6.150.214:18080/text2voice";
        String url =urlpre+":18080/text2voice";
        RestTemplate restTemplate = new RestTemplate();
        //定义参数
        Map<String, Object> params = new HashMap<>();
        params.put("text", text);
        //定义headers
        HttpHeaders headers = new HttpHeaders();
        headers.setContentType(MediaType.APPLICATION_JSON_UTF8);
        HttpEntity<Map<String, Object>> requestEntity = new HttpEntity<>(params, headers);
//        System.out.println(requestEntity.getBody());
        //执行HTTP请求
        try {
            ResponseEntity<String> response = restTemplate.exchange(url, HttpMethod.POST, requestEntity, String.class);
            JSONObject jsonObject = (JSONObject) JSONObject.parse( response.getBody());
            return jsonObject;

        } catch (RestClientException | JSONException e) {
            return null;
        }
    }

