# 将base64按每102400切片发送
def split_string(string):
    string_list = []
    if len(string) <= 102400:
        string_list.append(string)
        return string_list
    while len(string) > 102400:
        temp = string[0:102400]
        string = string[102400:]
        string_list.append(temp)
    if len(string) > 0:
        string_list.append(string)
        return string_list


def wav2pcm(wavfile, pcmfile, data_type=np.int16):
    f = open(wavfile, "rb")
    f.seek(0)
    f.read(44)
    data = np.fromfile(f, dtype=data_type)
    data.tofile(pcmfile)


